/**
 * 简单模态框：信息框、操作确认框、信息输入框
 * 此功能建立在jquery和bootstrap的模态框插件基础上
 * @author  luoluolzb
 * @version 2.0
 * @time    2019/5/25
 */
var lqModal={};$(function(){$(document.body).append($(`
<div id="lqModal-container">
 <!-- message Modal -->
 <div class="modal fade" id="lqModal-message-modal" tabindex="-1">
  <div class="modal-dialog">
   <div class="modal-content">
    <div class="modal-header">
     <h5 class="modal-title"></h5>
     <button type="button" class="close" data-dismiss="modal"><span aria-hidden="true">&times;</span></button>
    </div>
    <div class="modal-body"></div>
    <div class="modal-footer">
     <button type="button" class="okBtn btn btn-primary" data-dismiss="modal">确定</button>
    </div>
   </div>
  </div>
 </div>
 
 <!-- dialog Modal -->
 <div class="modal fade" id="lqModal-dialog-modal" tabindex="-1">
  <div class="modal-dialog">
   <div class="modal-content">
    <div class="modal-header">
     <h5 class="modal-title"></h5>
     <button type="button" class="close" data-dismiss="modal"><span aria-hidden="true">&times;</span></button>
    </div>
    <div class="modal-body"></div>
    <div class="modal-footer">
     <button type="button" class="okBtn btn btn-primary" data-dismiss="modal">确定</button>
     <button type="button" class="cancelBtn btn btn-secondary" data-dismiss="modal">取消</button>
    </div>
   </div>
  </div>
 </div>
 
 <!-- input Modal -->
 <div class="modal fade" id="lqModal-input-modal" tabindex="-1">
  <div class="modal-dialog">
   <div class="modal-content">
    <div class="modal-header">
     <h5 class="modal-title"></h5>
     <button type="button" class="close" data-dismiss="modal"><span aria-hidden="true">&times;</span></button>
    </div>
    <div class="modal-body">
     <label></label>
     <input type="text" class="form-control" value="" />
    </div>
    <div class="modal-footer">
     <button type="button" class="okBtn btn btn-primary" data-dismiss="modal">确定</button>
     <button type="button" class="cancelBtn btn btn-secondary" data-dismiss="modal">取消</button>
    </div>
   </div>
  </div>
 </div>
</div>
 `));lqModal.modals={$message:$('#lqModal-message-modal'),$dialog:$('#lqModal-dialog-modal'),$input:$('#lqModal-input-modal'),};var $message=lqModal.modals.$message;var $dialog=lqModal.modals.$dialog;var $input=lqModal.modals.$input;lqModal.message=function(options){if(typeof options==='string'){options={text:options,};}
options=$.extend({title:'提示消息',text:'',ok:undefined,},options||{});$message.find('.modal-title').html(options.title);$message.find('.modal-body').html(options.text);$message.find('.okBtn').off('click').on('click',options.ok);$message.modal('show');};lqModal.dialog=function(options){options=$.extend({title:'操作提示',text:'',ok:undefined,cancel:undefined,},options||{});$dialog.find('.modal-title').html(options.title);$dialog.find('.modal-body').html(options.text);$dialog.find('.okBtn').off('click').on('click',options.ok);$dialog.find('.cancelBtn').off('click').on('click',options.cancel);$dialog.modal('show');};lqModal.input=function(options){options=$.extend({title:'输入信息',text:'',ok:undefined,cancel:undefined,input:{type:'text',value:'',placeholder:'',},},options||{});$input.find('.modal-title').html(options.title);$input.find('label').html(options.text);$input.find('input').attr(options.input).val(options.input.value);$input.find('.okBtn').off('click').on('click',options.ok);$input.find('.cancelBtn').off('click').on('click',options.cancel);$input.modal('show');};lqModal.modal=function(type,options){var func=lqModal[type];if(func&&typeof func==='function'){return func(options);}};lqModal.getInput=function(){return $input.find('input').val();};});